* {
  margin: 0;
  padding: 0;
  box-sizing: border-box; }

#player {
  background-color: #060a3d;
  height: 100vh;
  overflow: hidden; }
  #player .header {
    text-align: center;
    height: 110px; }
    #player .header h1 {
      color: #fff;
      font-size: 20px;
      padding-top: 20px; }
    #player .header p {
      color: #868aaf;
      font-size: 12px; }
    #player .header .balls {
      display: flex;
      justify-content: center;
      align-items: center;
      margin-top: 20px; }
      #player .header .balls span {
        display: block;
        width: 5px;
        height: 5px;
        border-radius: 50%;
        background: #69134f;
        margin: 0 4px; }
        #player .header .balls span.current {
          width: 8px;
          height: 8px;
          background: #db3aaa; }
  #player .panels {
    height: calc(100vh - 270px);
    width: 200vw;
    display: flex;
    transition: transform 0.3s;
    align-items: center; }
    #player .panels.panel1 {
      transform: translate(0vw); }
    #player .panels.panel2 {
      transform: translateX(-100vw); }
    #player .panels .panel-effect {
      width: 100vw;
      height: 100%;
      display: flex;
      flex-direction: column;
      justify-content: center; }
      #player .panels .panel-effect .effect {
        position: relative;
        display: flex;
        justify-content: center;
        align-items: center;
        height: 40vh;
        width: 100%; }
        #player .panels .panel-effect .effect > div {
          background-size: contain;
          position: absolute; }
        #player .panels .panel-effect .effect .effect-1 {
          background: url("e7d0c241bc4265a7e8ceb95c38908003.svg") 0 0 no-repeat;
          width: 70vw;
          height: 70vw;
          animation: rotate 20s linear infinite; }
        #player .panels .panel-effect .effect .effect-2 {
          background: url("83f7f1ccd05df6601244a77026409602.svg") 0 0 no-repeat;
          width: 60vw;
          height: 60vw;
          animation: rotate 10s linear infinite reverse; }
        #player .panels .panel-effect .effect .effect-3 {
          background: url("e429ee394c7d0b1186a5f907ffaae7d6.svg") 0 0 no-repeat;
          width: 24vw;
          height: 24vw;
          animation: rotate 10s linear infinite; }
      #player .panels .panel-effect .lyrics {
        text-align: center; }
        #player .panels .panel-effect .lyrics p {
          color: #868aaf;
          font-size: 13px;
          margin-top: 10px; }
          #player .panels .panel-effect .lyrics p.current {
            color: #fff; }
    #player .panels .panel-lyrics {
      width: 100vw;
      height: 100%;
      overflow: hidden;
      text-align: center;
      line-height: 2; }
      #player .panels .panel-lyrics .container {
        transition: all .3s; }
        #player .panels .panel-lyrics .container p {
          font-size: 14px;
          color: #868aaf; }
          #player .panels .panel-lyrics .container p.current {
            color: #fff; }
  #player .footer {
    height: 160px; }
    #player .footer .button {
      display: flex;
      justify-content: center; }
      #player .footer .button svg {
        width: 24px;
        height: 24px;
        margin: 0 20px; }
    #player .footer .area-bar {
      color: #fff;
      font-size: 12px;
      display: flex;
      align-items: center;
      padding: 0 20px;
      margin-top: 20px; }
      #player .footer .area-bar .time-start {
        order: 1; }
      #player .footer .area-bar .time-end {
        order: 3; }
      #player .footer .area-bar .bar {
        order: 2;
        flex: 1;
        height: 4px;
        background: #0025f1;
        border-radius: 0 10px;
        margin: 0 10px;
        position: relative; }
        #player .footer .area-bar .bar .progress {
          width: 30%;
          height: 100%;
          border-radius: 2px;
          background: #db3aaa;
          position: relative; }
          #player .footer .area-bar .bar .progress::after {
            content: '';
            display: block;
            position: absolute;
            right: 0;
            top: -7px;
            width: 16px;
            height: 16px;
            background: url("87bc25daf95e06164a200b5f553c693d.svg") 0 0 no-repeat;
            background-size: contain; }
    #player .footer .action {
      display: flex;
      align-items: center;
      justify-content: space-between;
      margin-top: 20px;
      padding: 0 20px; }
      #player .footer .action svg {
        width: 28px;
        height: 28px; }
        #player .footer .action svg.btn-play-pause {
          width: 50px;
          height: 50px; }

@keyframes rotate {
  0% {
    transform: rotate(0); }
  100% {
    transform: rotate(360deg); } }
