import "./icons"
import Swiper from './swiper'
const $ = selector => document.querySelector(selector)
const $$ = selector => document.querySelectorAll(selector)

class Player {
  constructor(node) {
    this.root = typeof node === "string" ? $(node) : node
    this.songList = []
    this.currentIndex = 0
    this.audio = new Audio()
    this.start()
    this.bind()
  }

  start() {
   fetch("https://jirengu.github.io/data-mock/huawei-music/music-list.json")
      .then(res => res.json())
      .then(data => {
        this.songList = data
        this.audio.src = this.songList[this.currentIndex].url
      })
  }

  bind() {
    let self = this
    this.root.querySelector(".btn-play-pause").onclick = function () {
      if (this.classList.contains("play")) {
        self.audio.pause()
        this.classList.remove("play")
        this.classList.add("pause")
        this.querySelector("use").setAttribute("xlink:href", "#icon-play")
      } else if (this.classList.contains("pause")) {
        this.classList.remove("pause")
        this.classList.add("play")
        this.querySelector("use").setAttribute("xlink:href", "#icon-pause")
        self.audio.play()
      }
    }

    this.root.querySelector('.btn-pre').onclick = function(){
      self.playPrevSong()
    }

    this.root.querySelector('.btn-next').onclick = function(){
      self.playNextSong()
    }

    let swiper = new Swiper(this.root.querySelector('.panels'))
    swiper.on('swipLeft',function(){
      this.classList.remove('panel1')
      this.classList.add('panel2')
    })
    swiper.on('swipRight',function(){
      this.classList.remove('panel2')
      this.classList.add('panel1')
    })
  }

  playPrevSong() {
    this.currentIndex = (this.currentIndex-1+this.songList.length) % this.songList.length
    this.audio.src = this.songList[this.currentIndex].url
    console.log(this.songList[this.currentIndex])
    this.audio.oncanplaythrough = ()=>this.audio.play()
  }

  playNextSong(){
    this.currentIndex = (this.currentIndex+1+this.songList.length) % this.songList.length
    this.audio.src = this.songList[this.currentIndex].url
    console.log(this.songList[this.currentIndex])
    this.audio.oncanplaythrough = ()=>this.audio.play()
  }
}

new Player("#player")
